// Dependencies
import React, { Component } from "react";
import { Link } from "react-router-dom";
import Utils from "../utils/utils";

// Redux
import PropTypes from "prop-types";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";

// Material UI
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";

// Custom Actions


// START IMPORT ACTIONS
import DriverActions from "../redux/actions/DriverActions";

// END IMPORT ACTIONS

/** APIs

* actionsDriver.create
*	@description CRUD ACTION create
*
* actionsDriver.get
*	@description CRUD ACTION get
*	@param ObjectId id - Id resource
*
* actionsDriver.update
*	@description CRUD ACTION update
*	@param ObjectId id - Id
*

**/

class DriverEdit extends Component {
  // Init driver
  constructor(props) {
    super(props);
    this.state = {
      driver: {}
    };
  }

  // Load data on start
  componentDidMount() {
    if (this.props.match.params.id !== "new") {
      this.props.actionsDriver.loadDriver(this.props.match.params.id);
    }
    
  }

  // Insert props driver in state
  componentWillReceiveProps(props) {
    this.setState(...this.state, {
      driver: props.driver
    });
  }

  // Save data
  save(event) {
    event.preventDefault();
    if (this.state.driver._id) {
      this.props.actionsDriver.saveDriver(this.state.driver).then(data => {
        this.props.history.push("/drivers/");
      });
    } else {
      this.props.actionsDriver.createDriver(this.state.driver).then(data => {
        this.props.history.push("/drivers/");
      });
    }
  }

  // Show content
  render() {
    return (
      <div>
        <h1>Driver Edit</h1>
        <form className="myForm" onSubmit={this.save.bind(this)}>

          
          <TextField
            id="Name"
            label="Name"
            value={this.state.driver.Name || ""}
            onChange={Utils.handleChange.bind(this, "driver")}
            margin="normal"
            fullWidth
            required
            {...(!this.state.driver.Name && this.state.driver.Name === ""
              ? { error: true }
              : {})}
          />
          
          <FormControl fullWidth required
              {...(!this.state.driver.Status && !this.state.driver.Status
                ? { error: true }
                : {})}>
            <InputLabel shrink htmlFor="Status">
              Status
            </InputLabel>
            <Select
              value={this.state.driver.Status || ""}
              onChange={Utils.handleChangeSelect.bind(this, "driver")}
              inputProps={{
                id: "Status",
                name: "Status"
              }}
              fullWidth
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value={"Booked"}>Booked</MenuItem>
              <MenuItem value={"DayOff"}>DayOff</MenuItem>
              <MenuItem value={"Idle"}>Idle</MenuItem>
              <MenuItem value={"Leaved"}>Leaved</MenuItem>
            </Select>
          </FormControl>

          {/* Footer */}
          <div className="footer-card">
            <Link to="/drivers/">Back to list</Link>

            <Button type="submit" variant="contained" color="primary">
              Save
            </Button>
          </div>
        </form>
      </div>
    );
  }
}

// Store actions
const mapDispatchToProps = function(dispatch) {
  return { 
    actionsDriver: bindActionCreators(DriverActions, dispatch),
  };
};

// Validate types
DriverEdit.propTypes = { 
  actionsDriver: PropTypes.object.isRequired,
};

// Get props from state
function mapStateToProps(state, ownProps) {
  return {
    driver: state.DriverEditReducer.driver
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(DriverEdit);
